create Procedure Trae_Cartera_Clientes_CpbtDoc_Grupo(@ccb_codemp integer, @ccb_pclid integer, @ccb_ctcid integer, @ccb_estcpbt char(1), @idi_idid integer) as    SELECT Distinct         cartera_clientes_documentos_cpbt_doc.ccb_numagrupa      FROM cartera_clientes_documentos_cpbt_doc       WHERE ( cartera_clientes_documentos_cpbt_doc.ccb_codemp = @ccb_codemp ) AND             ( cartera_clientes_documentos_cpbt_doc.ccb_pclid = @ccb_pclid ) AND             ( cartera_clientes_documentos_cpbt_doc.ccb_ctcid = @ccb_ctcid  and ccb_estcpbt = @ccb_estcpbt and tci_idid =  @idi_idid and eci_idid  = @idi_idid and mci_idid =  @idi_idid and eci_estid > 1 )  order by ccb_numagrupa 