create Procedure Trae_Ente_Rol(@ejr_codemp integer, @ejr_rolid integer) as    SELECT Top 1 empleados.epl_rut,              empleados.epl_nombre,              empleados.epl_apepat,              empleados.epl_apemat        FROM entejud_rol,              entes_judicial,              empleados,              estados_empleado       WHERE ( entes_judicial.etj_codemp = entejud_rol.ejr_codemp ) and             ( entes_judicial.etj_etjid = entejud_rol.ejr_etjid ) and             ( empleados.epl_codemp = entes_judicial.etj_codemp ) and             ( empleados.epl_emplid = entes_judicial.etj_emplid ) and             ( estados_empleado.eem_codemp = empleados.epl_codemp ) and             ( estados_empleado.eem_eemid = empleados.epl_eemid ) and             ( ( entejud_rol.ejr_codemp = @ejr_codemp ) AND             ( entes_judicial.etj_abogado = 'S' ) AND             ( estados_empleado.eem_accion = 'A' ) AND             ( entejud_rol.ejr_rolid = @ejr_rolid )              )   