create Procedure Trae_Reporte_Cartera_Asig_Gestor(@ccb_codemp integer, @desde datetime, @hasta datetime, @ges_gesid integer) as    SELECT cartera_clientes_documentos_cpbt_doc.pcl_rut,              cartera_clientes_documentos_cpbt_doc.pcl_nomfant,              cartera_clientes_documentos_cpbt_doc.ctc_rut,              cartera_clientes_documentos_cpbt_doc.ctc_nomfant,              cartera_clientes_documentos_cpbt_doc.tci_nombre,              cartera_clientes_documentos_cpbt_doc.ccb_numero,              cartera_clientes_documentos_cpbt_doc.ccb_fecvenc,              cartera_clientes_documentos_cpbt_doc.ccb_monto,              cartera_clientes_documentos_cpbt_doc.ccb_saldo,              cartera_clientes_documentos_cpbt_doc.mon_nombre,              gestor.ges_nombre        FROM cartera_clientes_documentos_cpbt_doc,              gestor,              gestor_cartera       WHERE ( gestor_cartera.gsc_codemp = gestor.ges_codemp ) and             ( gestor_cartera.gsc_sucid = gestor.ges_sucid ) and             ( gestor_cartera.gsc_gesid = gestor.ges_gesid ) and             ( cartera_clientes_documentos_cpbt_doc.ccb_codemp = gestor_cartera.gsc_codemp ) and             ( cartera_clientes_documentos_cpbt_doc.ccb_pclid = gestor_cartera.gsc_pclid ) and             ( cartera_clientes_documentos_cpbt_doc.ccb_ctcid = gestor_cartera.gsc_ctcid ) and             ( ( cartera_clientes_documentos_cpbt_doc.ccb_codemp = @ccb_codemp ) AND             ( gestor_cartera.gsc_fecasig >= @desde ) AND             ( gestor_cartera.gsc_fecasig <= @hasta ) AND             ( gestor.ges_gesid = @ges_gesid )              )   