create Procedure Trae_Reporte_Documentos_Pendientes_Remesa(@ddi_codemp integer, @ddi_sucid integer, @dcc_tpcid integer, @dcc_numero integer) as   SELECT DISTINCT view_documentos_diarios.tci_nombre,              view_documentos_diarios.ddi_numcta,              view_documentos_diarios.ddi_monto,              view_documentos_diarios.bco_nombre,              view_documentos_diarios.ctc_rut,              view_documentos_diarios.ctc_nomfant,              view_documentos_diarios.ctc_numero,              view_documentos_diarios.ctc_digito,           view_documentos_diarios.ddi_fecvenc           FROM detalle_comprobantes,              aplicaciones_items,              view_documentos_diarios       WHERE ( aplicaciones_items.api_codemp = detalle_comprobantes.dcc_codemp ) and             ( aplicaciones_items.api_sucid = detalle_comprobantes.dcc_sucid ) and             ( aplicaciones_items.api_anio = detalle_comprobantes.dcc_anio ) and             ( aplicaciones_items.api_numapl = detalle_comprobantes.dcc_numapl ) and             ( aplicaciones_items.api_item = detalle_comprobantes.dcc_itemapl ) and             ( aplicaciones_items.api_codemp = view_documentos_diarios.ddi_codemp ) and             ( aplicaciones_items.api_sucid = view_documentos_diarios.ddi_sucid ) and             ( aplicaciones_items.api_aniodoc = view_documentos_diarios.ddi_anio ) and             ( aplicaciones_items.api_numdoc = view_documentos_diarios.ddi_numdoc ) and             ( ( detalle_comprobantes.dcc_tpcid = @dcc_tpcid ) AND             ( detalle_comprobantes.dcc_numero = @dcc_numero and ddi_codemp = @ddi_codemp and  ddi_sucid = @ddi_sucid)              )     order by ddi_fecvenc   