create Procedure Update_Aplicaciones_Item_Remesa(@dcc_codemp integer, @dcc_sucid integer, @dcc_tpcid integer, @dcc_numero numeric(15), @api_remesa char(1)) as    UPDATE aplicaciones_items         SET api_remesa = @api_remesa      FROM aplicaciones_items,              detalle_comprobantes       WHERE ( detalle_comprobantes.dcc_codemp = aplicaciones_items.api_codemp ) and             ( detalle_comprobantes.dcc_sucid = aplicaciones_items.api_sucid ) and             ( detalle_comprobantes.dcc_anio = aplicaciones_items.api_anio ) and             ( detalle_comprobantes.dcc_numapl = aplicaciones_items.api_numapl ) and             ( detalle_comprobantes.dcc_itemapl = aplicaciones_items.api_item ) and             ( ( detalle_comprobantes.dcc_codemp = @dcc_codemp ) AND             ( detalle_comprobantes.dcc_sucid = @dcc_sucid ) AND             ( detalle_comprobantes.dcc_tpcid = @dcc_tpcid ) AND             ( detalle_comprobantes.dcc_numero = @dcc_numero )              )    