create Procedure Update_Cartera_Clientes_Cpbt_Doc_Int(@ccb_codemp integer, @ccb_pclid integer, @ccb_ctcid numeric(15), @ccb_ccbid integer, @ccb_intereses decimal(15,2)) as    UPDATE cartera_clientes_cpbt_doc         SET ccb_intereses = @ccb_intereses       WHERE ( cartera_clientes_cpbt_doc.ccb_codemp = @ccb_codemp ) AND             ( cartera_clientes_cpbt_doc.ccb_pclid = @ccb_pclid ) AND             ( cartera_clientes_cpbt_doc.ccb_ctcid = @ccb_ctcid ) AND             ( cartera_clientes_cpbt_doc.ccb_ccbid = @ccb_ccbid )   