create Procedure Update_Deudores_Telefonos_Estados(@ddt_codemp integer, @ddt_numero numeric(20), @ddt_estado char(1)) as    UPDATE deudores_telefonos         SET ddt_estado = @ddt_estado       WHERE ( deudores_telefonos.ddt_codemp = @ddt_codemp ) AND             ( deudores_telefonos.ddt_numero = @ddt_numero )           UPDATE deudores_contactos_telefonos         SET dct_estado =  @ddt_estado      WHERE ( deudores_contactos_telefonos.dct_codemp =  @ddt_codemp ) AND             ( deudores_contactos_telefonos.dct_numero = @ddt_numero )                