create Procedure Update_Empleado_Asistencia_Horas(@eah_codemp integer, @eah_sucid integer, @eah_emplid integer, @eah_dia datetime,                                                                                             @eah_item smallint, @eah_tipoid integer, @eah_entrada datetime, @eah_salida datetime,                                                                                            @eah_authora char (1), @eah_pagsueldo char (1)) as    UPDATE empleado_asistencia_horas         SET eah_tipoid = @eah_tipoid,              eah_entrada = @eah_entrada,              eah_salida = @eah_salida,              eah_authora = @eah_authora,              eah_pagsueldo = @eah_pagsueldo       WHERE ( empleado_asistencia_horas.eah_codemp = @eah_codemp ) AND             ( empleado_asistencia_horas.eah_sucid = @eah_sucid ) AND             ( empleado_asistencia_horas.eah_emplid = @eah_emplid ) AND             ( datepart(year, empleado_asistencia_horas.eah_dia) = datepart(year, @eah_dia) ) AND             ( datepart(month, empleado_asistencia_horas.eah_dia) = datepart(month, @eah_dia) ) AND             ( datepart(day, empleado_asistencia_horas.eah_dia) = datepart(day, @eah_dia) ) AND             ( empleado_asistencia_horas.eah_item = @eah_item )  