create Procedure Update_Gestor_Cartera(@gsc_codemp integer, @gsc_sucid integer, @gsc_gesid integer, @gsc_ctcid numeric (15), @gsc_pclid integer) as     UPDATE gestor_cartera         SET gsc_fecasig = getdate(),               gsc_gesid =   @gsc_gesid,               gsc_pclid = @gsc_pclid      WHERE ( gestor_cartera.gsc_codemp = @gsc_codemp ) AND             ( gestor_cartera.gsc_sucid = @gsc_sucid ) AND             ( gestor_cartera.gsc_ctcid = @gsc_ctcid ) AND           ( gestor_cartera.gsc_pclid = @gsc_pclid ) 