create  Procedure Update_Monedas(@mon_codemp integer, @mon_codmon integer, @mon_nombre varchar (50), @mon_simbolo varchar (5), @mon_default char (1), @mon_porcint decimal(10,3), @mon_decimales smallint) as      UPDATE monedas         SET mon_nombre = @mon_nombre,              mon_simbolo = @mon_simbolo,              mon_default = @mon_default,           mon_porcint = @mon_porcint,           mon_decimales = @mon_decimales     WHERE ( monedas.mon_codemp = @mon_codemp ) AND             ( monedas.mon_codmon = @mon_codmon )  